# –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ –≤–∞–∫–∞–Ω—Å–∏–π –ø–æ –∫–ª—é—á–µ–≤—ã–º —Å–ª–æ–≤–∞–º
def filter_vacancies_by_specialty(specialty):
    keywords = specialty_keywords.get(specialty, [])
    if not keywords:
        return pd.DataFrame()  # –ï—Å–ª–∏ –∫–ª—é—á–µ–≤—ã—Ö —Å–ª–æ–≤ –Ω–µ—Ç –¥–ª—è —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç–æ–π DataFrame
    filtered_df = df_new[df_new['name'].str.contains('|'.join(keywords), case=False, na=False)]
    return filtered_df

def page_find_vacancies():
# –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–ª—è –≤—ã–±–æ—Ä–∞ —Ñ–∞–∫—É–ª—å—Ç–µ—Ç–∞ –∏ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏
    st.title("üéØ–ü–æ–∏—Å–∫ –≤–∞–∫–∞–Ω—Å–∏–π –¥–ª—è —Å—Ç—É–¥–µ–Ω—Ç–æ–≤ –∏ –≤—ã–ø—É—Å–∫–Ω–∏–∫–æ–≤")

# –®–∞–≥ 1: –í—ã–±–æ—Ä —Ñ–∞–∫—É–ª—å—Ç–µ—Ç–∞
    faculty = st.selectbox("üè´ –í—ã–±–µ—Ä–∏—Ç–µ —Ñ–∞–∫—É–ª—å—Ç–µ—Ç", prof_df['–§–∞–∫—É–ª—å—Ç–µ—Ç'].unique())

# –®–∞–≥ 2: –í—ã–±–æ—Ä —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏ (–ø—Ä–æ—Ñ–µ—Å—Å–∏–∏)
    specialty = st.selectbox("üíº –í—ã–±–µ—Ä–∏—Ç–µ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç—å", prof_df[prof_df['–§–∞–∫—É–ª—å—Ç–µ—Ç'] == faculty]['–°–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç—å'].unique())

# –®–∞–≥ 3: –í—ã–±–æ—Ä –≥–æ—Ä–æ–¥–∞
    city_name = st.selectbox("üìç –í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥", df_new['city'].unique())

# –®–∞–≥ 4: –ü–æ–∫–∞–∑–∞—Ç—å –ø–æ–¥—Ö–æ–¥—è—â–∏–µ –≤–∞–∫–∞–Ω—Å–∏–∏
    if st.button("üîé–ü–æ–∫–∞–∑–∞—Ç—å –≤–∞–∫–∞–Ω—Å–∏–∏"):
    # –§–∏–ª—å—Ç—Ä—É–µ–º –≤–∞–∫–∞–Ω—Å–∏–∏ –ø–æ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏
        filtered_df = filter_vacancies_by_specialty(specialty)
    
    # –§–∏–ª—å—Ç—Ä—É–µ–º –≤–∞–∫–∞–Ω—Å–∏–∏ –ø–æ –≥–æ—Ä–æ–¥—É
        filtered_df = filtered_df[filtered_df['city'] == city_name]
    
        if not filtered_df.empty:
            st.write("‚úÖ–ö–æ–º–ø–∞–Ω–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –∏—â—É—Ç —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç–æ–≤ –ø–æ –≤—ã–±—Ä–∞–Ω–Ω–æ–π —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏:")

        # –û—Ç–æ–±—Ä–∞–∑–∏–º –∫–æ–º–ø–∞–Ω–∏–∏ —Å —Å—Å—ã–ª–∫–∞–º–∏ –Ω–∞ –≤–∞–∫–∞–Ω—Å–∏–∏
            for _, row in filtered_df.iterrows():
                company_name = row['company']
                vacancy_link = row['url']  # –ü—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ–º, —á—Ç–æ –≤ 'url' –µ—Å—Ç—å —Å—Å—ã–ª–∫–∞ –Ω–∞ –≤–∞–∫–∞–Ω—Å–∏–∏
                st.markdown(f"[{company_name}]({vacancy_link})")
        else:
            st.write("üôÅ –ù–µ—Ç –≤–∞–∫–∞–Ω—Å–∏–π, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏—Ö –≤–∞—à–∏–º –∫—Ä–∏—Ç–µ—Ä–∏—è–º.")

